import React, { useState, useEffect } from "react";
import { collection, query, where, orderBy, getDocs } from "firebase/firestore";
import { firestore } from "../firebase"; // Make sure to import firestore correctlyimport { useAuth } from "./AuthProvider";
import jsPDF from "jspdf";

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);
  const [filter, setFilter] = useState("");
  const { currentUser } = useAuth();

  useEffect(() => {
    const fetchData = async () => {
      const data = await db
        .collection("transactions")
        .where("userId", "==", currentUser.uid)
        .orderBy("date", "desc")
        .get();
      setTransactions(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };
    fetchData();
  }, [currentUser]);

  const filteredTransactions = transactions.filter(
    (tx) =>
      tx.name.includes(filter) ||
      tx.type.includes(filter) ||
      tx.method.includes(filter)
  );

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text("Report delle Transazioni", 10, 10);
    transactions.forEach((tx, index) => {
      doc.text(
        `${index + 1}. ${tx.name} - ${tx.type} - ${tx.date}`,
        10,
        20 + index * 10
      );
    });
    doc.save("report.pdf");
  };

  return (
    <div>
      <h2>Transazioni</h2>
      <button onClick={generatePDF}>Genera Report PDF</button>
      <input
        type="text"
        placeholder="Cerca..."
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
      />
      <ul>
        {filteredTransactions.map((tx) => (
          <li key={tx.id}>
            <p>Nome: {tx.name}</p>
            <p>Tipo: {tx.type}</p>
            <p>Data: {tx.date}</p>
            <p>Metodo: {tx.method}</p>
            <p>Note: {tx.notes}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionList;
